    international-track


        ((SELECT osm_id,way,route,name,ref,network,char_length(ref) AS length FROM &prefix;_line WHERE osm_id > 0 AND
	  route='foot' AND (network='e-road' OR network='nwn' OR network='iwn')) UNION
        (SELECT osm_id,way,route,name,ref,network,char_length(ref) AS length from &prefix;_track_rels WHERE
	  route='foot' AND (network='e-road' OR network='nwn' OR network='iwn'))) as international_tacks

      &datasource-settings;





    nobicycle


        (select osm_id,way,highway,bicycle
        from planet_osm_line
        where (bicycle = 'no') or (highway in ('trunk','motorway','trunk_link', 'motorway_link') and (bicycle is null or bicycle not in ('yes','designated')))) as nobicycle

        &datasource-settings;



    mtb_incline


        (select osm_id,way,incline,"mtb:scale:uphill", "mtb:scale"
        from planet_osm_line
        where (("mtb:scale:uphill" in ('3', '4', '5') and incline in ('up','down'))
        or ("mtb:scale" in ('2-', '2', '2+', '3-', '3', '3+', '4-', '4', '4+', '5-', '5', '5+') and incline in ('up','down')))) as mtb_incline

        &datasource-settings;



    mtb_shield5


        (select osm_id,way
        from planet_osm_line
        where ("mtb:scale" is not null or mtb is not null or
        "mtb:scale:uphill" is not null or route='mtb') and "mtb:scale" in ('5','5+','5-')) as mtb_basic

        &datasource-settings;



    mtb_shield4


        (select osm_id,way
        from planet_osm_line
        where ("mtb:scale" is not null or mtb is not null or
        "mtb:scale:uphill" is not null or route='mtb') and "mtb:scale" in ('4','4+','4-')) as mtb_basic

        &datasource-settings;



    mtb_shield3


