        from planet_osm_line
        where ("mtb:scale" is not null or mtb is not null or
        "mtb:scale:uphill" is not null or route='mtb') and "mtb:scale" in ('3','3+','3-')) as mtb_basic

        &datasource-settings;



    mtb_shield2


