access


      (select way,access,highway from planet_osm_line where access is not null) as access

      &datasource-settings;




trams


      (select way from planet_osm_line where railway='tram' and (tunnel is null or tunnel not in ('yes','true','1'))) as trams

      &datasource-settings;




guideways


      (select way from planet_osm_line where highway='bus_guideway' and (tunnel is null or tunnel not in ('yes','true','1'))) as guideways

      &datasource-settings;




stations


      (select way,name,railway,aerialway,disused
      from planet_osm_point
      where railway in ('station','halt','tram_stop')
         or aerialway='station'
      ) as stations

      &datasource-settings;




stations


      (select way,name,railway,aerialway,disused
      from planet_osm_polygon
      where railway in ('station','halt','tram_stop')
         or aerialway='station'
      ) as stations

      &datasource-settings;




power

      (select way from planet_osm_line where "power"='line') as power
      &datasource-settings;




power_towers

      (select way from planet_osm_point where power='tower') as power_towers
      &datasource-settings;




directions


      (select way,
       case when oneway in ('yes','true','1') then 'yes'::text else oneway end as oneway
       from planet_osm_line
       where oneway is not null
         and (highway is not null or railway is not null or waterway is not null)
      ) as directions

      &datasource-settings;



   bicycle_directions


         (select way,
       case when oneway in ('yes','true','1') then 'yes'::text else oneway end as oneway,
       case when "oneway:bicycle" in ('no','false') then 'no'::text end as "oneway:bicycle",
       case when cycleway in ('opposite', 'opposite_lane') then 'opposite'::text end as cycleway
         from planet_osm_line
       where (oneway is not null) and (cycleway is not null)
         and (highway is not null or railway is not null or waterway is not null)
      ) as bicycle_directions

      &datasource-settings;





admin-01234


      (select way,admin_level
       from planet_osm_roads
       where "boundary"='administrative'
         and admin_level in ('2', '3')
       ) as admin
      &datasource-settings;







suburb
village
hamlet


      (select way,place,name
      from planet_osm_point
      where place in ('suburb','village','large_village','hamlet','locality')
      ) as placenames
      &datasource-settings;




city
town


      (select way,place,upper(name),name
      from planet_osm_point
      where place in ('city','metropolis','town','large_town','small_town')
        and (capital is null or capital<>'yes')
      ) as placenames
      &datasource-settings;




country
state
capital


      (select way,place,upper(name),name,ref
      from planet_osm_point
      where place in ('country','state')
         or (place in ('city','metropolis') and capital='yes')
      ) as placenames
      &datasource-settings;




    stations    
    symbols
    points

      (select * from &prefix;_point where barrier IS NOT NULL or amenity IS NOT NULL or tourism is NOT NULL or information is NOT NULL or railway is NOT NULL or "natural" is NOT NULL or highway is NOT NULL or aeroway is NOT NULL or aerialway is NOT NULL or power_source is NOT NULL or man_made is NOT NULL or shop is NOT NULL or cycleway is NOT NULL) as amenity
      &datasource-settings;



    stations
    symbols-poly
    points

      (select * from &prefix;_polygon where barrier IS NOT NULL or amenity IS NOT NULL or tourism is NOT NULL or railway is NOT NULL or "natural" is NOT NULL or highway is NOT NULL or aeroway is NOT NULL or aerialway is NOT NULL or power_source is NOT NULL or man_made is NOT NULL or shop is NOT NULL) as amenity
      &datasource-settings;






roads-text


      (select way,highway,aeroway,name,ref,admin_level,char_length(ref) as length,
       case when bridge in ('yes','true','1') then 'yes'::text else bridge end as bridge
       from planet_osm_line
       where waterway IS NULL
         and leisure IS NULL
         and landuse IS NULL
         and admin_level IS NULL
         and (name is not null or ref is not null)
      ) as roads

      &datasource-settings;




text


      (select way,amenity,highway,leisure,landuse,man_made,"natural",place,tourism,information,ele,name,ref,shop,military,aeroway,'yes'::text as point
       from planet_osm_point
       where amenity is not null
          or leisure is not null
          or landuse is not null
          or tourism is not null
	  or information is not null
          or "natural" is not null
          or man_made in ('lighthouse','windmill')
          or place='island'
          or highway='motorway_junction'
          or military='danger_area'
          or aeroway='gate'
          or shop='bicycle'
      ) as text

      &datasource-settings;




text-poly


      (select way,aeroway,amenity,highway,leisure,landuse,man_made,"natural",place,tourism,NULL as ele,name,ref,shop,military,'no'::text as point
       from planet_osm_polygon
       where amenity is not null
          or leisure is not null
          or landuse is not null
          or tourism is not null
          or "natural" is not null
          or man_made in ('lighthouse','windmill')
          or place='island'
          or military='danger_area'
          or shop='bicycle'
      ) as text

      &datasource-settings;




area-text


      (select way,way_area,name
       from planet_osm_polygon
       where name is not null
         and (waterway is null or waterway <> 'riverbank')
         and admin_level is null
       order by z_order,way_area desc
      ) as text

      &datasource-settings;





    cycleway-text


        (select way, ref, name, lcn, ncn, rcn, state
        from planet_osm_line
        where ref is not null and (lcn is not null or ncn is not null or rcn is not null)) as cycleway

        &datasource-settings;




boundary


      (select way,name from planet_osm_polygon where boundary='national_park') as boundary

      &datasource-settings;
