Datasource.Parameter:
(select ST_MakeLine(p1.way,
(select ST_Centroid(ST_ConvexHull(ST_Collect(p3.way))) as way from planet_osm_point as p3 join planet_osm_station_points_rels as p4 on p3.osm_id = p4.osm_id where ((p1.highway='bus_stop' or p1.amenity = 'bus_stop') and (p4.highway='bus_stop' or p4.amenity = 'bus_stop')) and p4.rel_id = p2.rel_id group by p4.rel_id, p4.name)
) as way, p1.highway, p1.railway, p1.amenity from planet_osm_station_points_rels as p1 join planet_osm_station_points_rels as p2 on p1.osm_id = p2.osm_id where (p1.highway='bus_stop' or p1.amenity = 'bus_stop') ) as points

(select ST_MakeLine(p1.way,
(select ST_Centroid(ST_ConvexHull(ST_Collect(p3.way))) as way from planet_osm_point as p3 join planet_osm_station_points_rels as p4 on p3.osm_id = p4.osm_id where ((p1.railway='tram_stop') and (p4.railway='tram_stop')) and p4.rel_id = p2.rel_id group by p4.rel_id, p4.name)
) as way, p1.highway, p1.railway, p1.amenity from planet_osm_station_points_rels as p1 join planet_osm_station_points_rels as p2 on p1.osm_id = p2.osm_id where (p1.railway='tram_stop') ) as points
